#Cell
import torch
class Vertex(object):
    '''Implements vertex. Stores vertex position

    *Methods*:
        Vertex.
    '''
    def __init__(self,location=None):
        '''access `location` with self.x '''
        self._x=location

    def dist(self,other):
        '''Calculate (Euclidean) distance to another vertex'''
        return torch.sqrt(torch.sum(torch.pow(other.x - self._x,2)))

    @property
    def x(self):
        '''Get current location'''
        return self._x
    @x.setter
    def x(self, val):
        self._x = val

    def __str__(self):
        return f"{self._x.tolist()}"
    def __repr__(self):
        return f"{self._x};{self._x.dtype}"
